package d07_ForLoop;

public class C09_Q9 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int carpan1 = 1; carpan1 <= 1; carpan1++) {

			for (int carpan2 = 1; carpan2 <= 10; carpan2++)
				System.out.println(carpan1 + " x " + carpan2 + " = " + (carpan1 * carpan2));

			System.out.println();
		}
	}

}
