package day10_Ternary_SwitchCase;

public class C10_SwitchCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int sayi = 1000;
		switch (sayi) {
		case 10:
			System.out.println("En kucuk iki basamakli");
			break;
		case 100:
			System.out.println("En kucuk uc basamakli");
			break;
		case 1000:
			System.out.println("En kucuk dort basamakli");
			break;
		default:
			System.out.println("Lutfen sayiyi degistirin ");

		}
	}
}
